// Using string concatenation (inefficient)
string result = "";
for (int i = 0; i < 1000; i++) {
    result += i.ToString();
}

// Using StringBuilder (efficient)
StringBuilder stringBuilder = new StringBuilder();
for (int i = 0; i < 1000; i++) {
    stringBuilder.Append(i);
}
string efficientResult = stringBuilder.ToString();
